<template>
  <div class="oss-image">
    <a-spin :spinning="loading">
      <a-image
        crossOrigin="anonymous"
        :width="isThumbnail ? width : originWidth"
        :height="isThumbnail ? height : originHeight"
        v-bind="$attrs"
        v-if="fileId"
        :src="imgUrlThumb"
        :fallback="fallback"
        :preview="currentPreview"
      />
    </a-spin>
  </div>
</template>

<script lang="ts" setup>
  import { ref, watch } from 'vue';
  import { bool, string } from 'vue-types';
  import { getFile0ssPreviewThumbnail, getFileOssPreview } from '@/api/manageCenter/fileManage';

  const props = defineProps({
    fileId: {
      type: String,
      default: '',
    },
    /**
     * 缩略图宽，isThumbnail=true 有效
     */
    width: {
      type: Number,
      default: 200,
    },
    /**
     * 缩略图高，isThumbnail=true 有效
     */
    height: {
      type: Number,
      default: 200,
    },
    /**
     * 原图宽，isThumbnail=false 有效
     */
    originWidth: {
      type: String,
      default: '100%',
    },
    /**
     * 原图高，isThumbnail=false 有效
     */
    originHeight: {
      type: String,
      default: 'auto',
    },
    /**
     * 0~100
     */
    quality: {
      type: Number,
      default: 85,
    },
    /**
     * 是否显示预览
     */
    preview: {
      type: Boolean,
      default: () => true,
    },
    /**
     * 是否是缩略图片，否则原图
     */
    isThumbnail: {
      type: Boolean,
      default: () => true,
    },
  });
  const currentPreview = ref();
  const imgUrlThumb = ref<string>('');
  const imgUrlOrigin = ref<string>('');
  const visible = ref<boolean>(false);
  const setVisible = (value): void => {
    if (!visible.value && value) {
      loadPreview();
    }
    visible.value = value;
  };
  const fallback =
    'data:image/png;base64,';
  const loading = ref<boolean>(false);

  // function createObjectURL(res) {
  //   const blob = new Blob([res]);
  //   return window.URL.createObjectURL(blob);
  // }

  // let preview = ref<Record<string, any>>({});
  /**
   * 检查fileID
   * @param fileId
   */
  function checkGUID(fileId?: string) {
    if (!fileId) return false;
    const guid = fileId.match(/^[0-9a-z]{8}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{12}$/i);
    return !!guid;
  }
  /**
   * 加载原图
   */
  function loadPreview() {
    if (imgUrlOrigin.value) {
      return;
    }
    loading.value = true;
    getFileOssPreview({ FileId: props.fileId })
      .then((r) => {
        imgUrlOrigin.value = r.url;
      })
      .finally(() => (loading.value = false));
  }
  /**
  监听 */
  watch(
    () => props.fileId,
    (newVal) => {
      if (!checkGUID(newVal)) {
        imgUrlThumb.value = newVal;
        imgUrlOrigin.value = newVal;
      } else {
        loading.value = true;

        (props.isThumbnail
          ? getFile0ssPreviewThumbnail({
              FileId: newVal,
              Width: props.width,
              Height: props.height,
              Quality: props.quality,
            })
          : getFileOssPreview({ FileId: newVal })
        )
          .then((r) => {
            imgUrlThumb.value = r.url;
          })
          .finally(() => {
            loading.value = false;
          });
      }
    },
    {
      immediate: true,
    },
  );

  watch(
    () => props.preview,
    (newVal) => {
      if (newVal) {
        currentPreview.value = {
          visible: visible,
          onVisibleChange: setVisible,
          src: imgUrlOrigin,
        };
      } else {
        currentPreview.value = false;
      }
    },
    {
      immediate: true,
    },
  );
</script>

<style lang="less" scoped></style>
